import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './note.css'

function Notes() {
  const [notes, setNotes] = useState([]);
  const [updateNoteId, setUpdateNoteId] = useState(null);
  const [updateNoteText, setUpdateNoteText] = useState('');

  useEffect(() => {
    const fetchNotes = async () => {
      try {
        const res = await axios.get('http://localhost:1400/getnotes');
        setNotes(res.data);
      } catch (err) {
        console.log(err);
      }
    };

    fetchNotes();
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:1400/notes/${id}`);
      setNotes(notes.filter(note => note._id !== id));
    } catch (err) {
      console.log(err);
    }
  };

  const handleUpdate = (id, text) => {
    setUpdateNoteId(id);
    setUpdateNoteText(text);
  };

  const handleUpdateSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.put(`http://localhost:1400/notes/${updateNoteId}`, { note: updateNoteText });
      setNotes(notes.map(note => (note._id === updateNoteId ? res.data.notes : note)));
      setUpdateNoteId(null);
      setUpdateNoteText('');
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <div className='full01'>
      <h2>NOTES</h2>

      {updateNoteId && (
        <form onSubmit={handleUpdateSubmit}>
          <input className='input02'
            type="text"
            value={updateNoteText}
            onChange={(e) => setUpdateNoteText(e.target.value)}
            placeholder="Update your note"
          />
          <div className='updatebutton'>
          <button className="submit02">Submit Update</button>
          </div>
        </form>
      )}
      <div className='card-container'>
        {notes.map((note, index) => (
          <div key={index} className='card'>
            <p>{note.note}</p>
            <p>{note.title}</p>
            <p>{note.content}</p>
            <p>{note.n}</p>
            <button onClick={() => handleUpdate(note._id, note.note)}>Update</button>
            <button onClick={() => handleDelete(note._id)}>Delete</button>
          </div>
        ))}
      </div>

     
    </div>
  );
}

export default Notes;
