import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom'; // Import useHistory for navigation
import './note.css';

function Notes() {
  const [notes, setNotes] = useState([]);
  const [updateNoteId, setUpdateNoteId] = useState(null);
  const [updateNoteText, setUpdateNoteText] = useState('');
  const history = useHistory(); // Initialize useHistory

  useEffect(() => {
    const fetchNotes = async () => {
      try {
        const res = await axios.get('http://localhost:1400/notes');
        setNotes(res.data);
      } catch (err) {
        console.log(err);
      }
    };

    fetchNotes();
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:1400/notes/${id}`);
      setNotes(notes.filter(note => note._id !== id));
    } catch (err) {
      console.log(err);
    }
  };

  const handleUpdate = (id, text) => {
    setUpdateNoteId(id);
    setUpdateNoteText(text);
    history.push(`/updatenote/${id}`); // Navigate to update note form with note id
  };

  return (
    <div className='full01'>
      <h2>NOTES</h2>
      <div className='card-container'>
        {notes.map((note) => (
          <div key={note._id} className='card'>
            <p>{note.note}</p>
            <p>{note.title}</p>
            <p>{note.content}</p>
            <p>{note.instruction}</p>
            <button onClick={() => handleUpdate(note._id, note.note)}>Update</button>
            <button onClick={() => handleDelete(note._id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Notes;
