import React from 'react';
import axios from 'axios';
import './form.css';

function UpdateForm({ updateNoteId, updateNoteText, handleUpdateSubmit, setUpdateNoteText }) {
  return (
    <div className='form-container'>
      {updateNoteId && (
        <form onSubmit={handleUpdateSubmit}>
          <div className='form-group'>
            <input
              className='bottom-border-input'
              type="text"
              value={updateNoteText}
              onChange={(e) => setUpdateNoteText(e.target.value)}
              placeholder="Update your note"
            />
          </div>
          <div className='form-group'>
            <button className="button01" type="submit">Submit Update</button>
          </div>
        </form>
      )}
    </div>
  );
}

export default UpdateForm;
